$.fn.extend({
	cccScrollBar: function() {
		var b = $(this);
		var a = b.find(".scrollContent").eq(0);
		var g = b.find(".sideBar").eq(0);
		var h = b.find(".slider").eq(0);
		var f = $(document);
		
		function c(i) {
			$(this).css("transform", "translate3d(0," + i + "px,0")
		}
		
		function e(j, k) {
			j = j >= 0 ? j : 0;
			j = j <= g.height() - h.height() ? j : g.height() - h.height();
			var i = -j * d;
			if(k) {
				a.add(h).addClass("animate")
			} else {
				a.add(h).removeClass("animate")
			}
			c.apply(h, [j]);
			c.apply(a, [i])
		}
		
		var d = b[0].scrollHeight / b.innerHeight();
		
		console.log((a.innerHeight() + (b.innerHeight() - b.height()) / 2), b[0].scrollHeight);
		if(d > 1) {
			g.addClass("active")
		}
		
		h.height(g.height() / d);
		g.on("mousedown", function(k) {
			var k = k || window.event;
			var j = $(this);
			var i = k.clientY - j.offset().top - h.height() / 2;
			e(i, true)
		});
		
		h.on("mousedown", function(j) {
			var j = j || window.event;
			j.stopPropagation();
			j.preventDefault();
			var i = j.clientY - $(this).offset().top;
			f.on("mousemove", function(l) {
				var l = l || window.event;
				var k = l.clientY - g.offset().top - i;
				e(k, false)
			});
			f.on("mouseup", function() {
				f.off("mousemove")
			})
		});
		
		b.on("mousewheel DOMMouseScroll", function(k) {
			var k = k || window.event;
			k.stopPropagation();
			k.preventDefault();
			var j = "down";
			if(k.originalEvent.wheelDelta < 0 || k.originalEvent.detail > 0) {
				j = "down"
			} else {
				j = "up"
			}
			var i = h.position().top;
			if(j == "down") {
				i += 10
			} else {
				i -= 10
			}
			e(i, false)
		});
		
		return b
	}
});


